const auth = $('Validate Auth').first().json;
const ADMIN_ROLES = ['super_admin','company_admin'];
if (!ADMIN_ROLES.includes(auth.role)) {
  return [{ json: { ...auth, error: 'Admin role required (super_admin or company_admin)' } }];
}
return [{ json: { ...auth } }];

